$(function(){$(".del").click(function(t){var a=$(t.target).data("id"),e=$(".item-id-"+a);$.ajax({type:"delete",url:"/admin/movie/list?id="+a}).done(function(t){1===t.success&&e.length>0&&e.remove()})}),$("#douban").blur(function(){var t=$(this).val();t&&$.ajax({url:"https://api.douban.com/v2/movie/subject/"+t,cache:!0,type:"get",dataType:"jsonp",crossDomain:!0,jsonp:"callback",success:function(t){$("#inputTitle").val(t.title),$("#inputDoctor").val(t.directors[0].name),$("#inputCountry").val(t.countries[0]),$("#inputPoster").val(t.images.large),$("#inputYear").val(t.year),$("#inputSummary").val(t.summary)}})})});